mapper.insert:

  SELECT LAST_INSERT_ID()

insert into device_material (device_id, channel_id, material_bucket, 
  material_name, material_oss_url, material_url, 
  material_date, created_time, is_delete
  )
values (#{deviceId,jdbcType=VARCHAR}, #{channelId,jdbcType=VARCHAR}, #{materialBucket,jdbcType=VARCHAR}, 
  #{materialName,jdbcType=VARCHAR}, #{materialOssUrl,jdbcType=VARCHAR}, #{materialUrl,jdbcType=VARCHAR}, 
  #{materialDate,jdbcType=VARCHAR}, #{createdTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=CHAR}
  )

mapper.select:
select
count(*)
from device_material


  device_id=#{deviceId,jdbcType=VARCHAR}


  AND channel_id=#{channelId,jdbcType=VARCHAR}


  AND material_bucket=#{materialBucket,jdbcType=VARCHAR}


  AND material_name=#{materialName,jdbcType=VARCHAR}


  AND material_oss_url=#{materialOssUrl,jdbcType=VARCHAR}


  AND material_url=#{materialUrl,jdbcType=VARCHAR}


  AND material_date=#{materialDate,jdbcType=VARCHAR}


  AND created_time=#{createdTime,jdbcType=TIMESTAMP}


  AND is_delete=#{isDelete,jdbcType=CHAR}

